import{_ as d,c as o,a3 as e,o as r}from"./chunks/framework.BOyF8YV7.js";const u=JSON.parse('{"title":"英语语法详解：完整版时态讲解及例句","description":"全面讲解英语时态，包括一般、进行、完成、完成进行四种基本时态，以及过去、现在、将来、过去将来四种时间维度下的各种时态用法，并配有例句说明。助你轻松掌握英语时态，提升英语语法水平。","frontmatter":{"head":[["link",{"rel":"canonical","href":"https://blog.ranchulin.com/t/英语/时态.html"}],["meta",{"property":"og:title","title":"英语语法详解：完整版时态讲解及例句"}],["meta",{"property":"og:url","content":"https://blog.ranchulin.com/t/英语/时态.html"}],["meta",{"name":"keywords","content":"时态,英语语法,动词,一般过去时,过去进行时,过去完成时,过去完成进行时,一般现在时,现在进行时,现在完成时,现在完成进行时,一般将来时,将来进行时,将来完成时,将来完成进行时,过去将来时,过去将来进行时,过去将来完成时,过去将来完成进行时,语法讲解,英语学习"}]]},"headers":[],"relativePath":"t/英语/时态.md","filePath":"t/英语/时态.md","lastUpdated":1736299776000}'),l={name:"t/英语/时态.md"};function a(h,t,n,i,s,c){return r(),o("div",null,t[0]||(t[0]=[e('<h2 id="时态" tabindex="-1">时态 <a class="header-anchor" href="#时态" aria-label="Permalink to &quot;时态&quot;">​</a></h2><p>表示行为、动作和状态，在各个时间条件下的动词形态</p><ul><li>一般</li><li>进行</li><li>完成</li><li>完成进行</li></ul><hr><ul><li>过去</li><li>现在</li><li>将来</li><li>过去将来</li></ul><table tabindex="0"><thead><tr><th>时态</th><th>语态</th><th>语法</th><th>例句</th></tr></thead><tbody><tr><td>过去</td><td>一般过去</td><td>主语 + 动词过去式</td><td>I went to school yesterday.</td></tr><tr><td>过去</td><td>过去进行</td><td>主语 + was/were + 动词 ing</td><td>I was going to school yesterday.</td></tr><tr><td>过去</td><td>过去完成</td><td>主语 + had + 过去分词</td><td>I had gone to school yesterday.</td></tr><tr><td>过去</td><td>过去完成进行</td><td>主语 + had + been + 动词 ing</td><td>I had been going to school yesterday.</td></tr><tr><td>现在</td><td>一般现在</td><td>主语 + 动词原形</td><td>I go to school everyday.</td></tr><tr><td>现在</td><td>现在进行</td><td>主语 + am/is/are + 动词 ing</td><td>I am going to school now.</td></tr><tr><td>现在</td><td>现在完成</td><td>主语 + have/has + 过去分词</td><td>I have gone to school.</td></tr><tr><td>现在</td><td>现在完成进行</td><td>主语 + have/has + been + 动词 ing</td><td>I have been going to school.</td></tr><tr><td>将来</td><td>一般将来</td><td>主语 + will + 动词原形</td><td>I will go to school tomorrow.</td></tr><tr><td>将来</td><td>将来进行</td><td>主语 + will + be + 动词 ing</td><td>I will be going to school tomorrow.</td></tr><tr><td>将来</td><td>将来完成</td><td>主语 + will + have + 过去分词</td><td>I will have gone to school tomorrow.</td></tr><tr><td>将来</td><td>将来完成进行</td><td>主语 + will + have + been + 动词 ing</td><td>I will have been going to school tomorrow.</td></tr><tr><td>过去将来</td><td>过去将来</td><td>主语 + would + 动词原形</td><td>I would go to school tomorrow.</td></tr><tr><td>过去将来</td><td>过去将来进行</td><td>主语 + would + be + 动词 ing</td><td>I would be going to school tomorrow.</td></tr><tr><td>过去将来</td><td>过去将来完成</td><td>主语 + would + have + 过去分词</td><td>I would have gone to school tomorrow.</td></tr><tr><td>过去将来</td><td>过去将来完成进行</td><td>主语 + would + have + been + 动词 ing</td><td>I would have been going to school tomorrow.</td></tr></tbody></table><h2 id="参考" tabindex="-1">参考 <a class="header-anchor" href="#参考" aria-label="Permalink to &quot;参考&quot;">​</a></h2><ul><li><a href="https://zhuanlan.zhihu.com/p/61394682" target="_blank" rel="nofollow noopener noreferrer">https://zhuanlan.zhihu.com/p/61394682</a></li><li><a href="https://zhuanlan.zhihu.com/p/381955930" target="_blank" rel="nofollow noopener noreferrer">https://zhuanlan.zhihu.com/p/381955930</a></li></ul>',8)]))}const m=d(l,[["render",a]]);export{u as __pageData,m as default};
