import{_ as a,c as e,a3 as i,o as t}from"./chunks/framework.BOyF8YV7.js";const m=JSON.parse('{"title":"新生儿出生医学证明及医保办理指南","description":"本文详细介绍了新生儿出生医学证明的办理流程，所需材料，时间地点以及新生儿医保的办理方法，包括网上预约挂号方式和所需材料，方便新手父母快速办理相关证件。","frontmatter":{"head":[["link",{"rel":"canonical","href":"https://blog.ranchulin.com/生活/育儿/出生证明.html"}],["meta",{"property":"og:title","title":"新生儿出生医学证明及医保办理指南"}],["meta",{"property":"og:url","content":"https://blog.ranchulin.com/生活/育儿/出生证明.html"}],["meta",{"name":"keywords","content":"新生儿,出生医学证明,办理出生医学证明,新生儿医保,户口本,医保,新生儿取名字,重庆市妇幼保健院,出生证明办理,新生儿证件"}]]},"headers":[],"relativePath":"生活/育儿/出生证明.md","filePath":"生活/育儿/出生证明.md","lastUpdated":1736299776000}'),r={name:"生活/育儿/出生证明.md"};function o(n,l,h,c,d,u){return t(),e("div",null,l[0]||(l[0]=[i('<h2 id="新生儿取名字" tabindex="-1">新生儿取名字 <a class="header-anchor" href="#新生儿取名字" aria-label="Permalink to &quot;新生儿取名字&quot;">​</a></h2><p>--</p><h2 id="出生证明" tabindex="-1">出生证明 <a class="header-anchor" href="#出生证明" aria-label="Permalink to &quot;出生证明&quot;">​</a></h2><blockquote><p>《出生医学证明》是申报户籍、取得新生儿社会保障、接受医疗服务、申办入托入学、申请出国移民留学等事项的依据和凭证之一。办理成功后，请宝宝父母妥善保管！</p></blockquote><h3 id="办理《出生医学证明》需要准备哪些材料" tabindex="-1">办理《出生医学证明》需要准备哪些材料？ <a class="header-anchor" href="#办理《出生医学证明》需要准备哪些材料" aria-label="Permalink to &quot;办理《出生医学证明》需要准备哪些材料？&quot;">​</a></h3><ul><li>父亲办理为代办，需要如下材料 <code>(家属代办，让他去前台找护士站的护士领取一张委托书)</code><ul><li>新生儿父母身份证原件及复印件（复印件需在有效期内并复印正反两面）</li><li>新生儿母亲签字按手印的委托书及领证人身份证原件与复印件（复印件需在有效期内并复印正反两面）</li><li>需要新生儿的名字（规范汉字）</li></ul></li></ul><h3 id="办理《出生医学证明》的时间、地点" tabindex="-1">办理《出生医学证明》的时间、地点？ <a class="header-anchor" href="#办理《出生医学证明》的时间、地点" aria-label="Permalink to &quot;办理《出生医学证明》的时间、地点？&quot;">​</a></h3><ul><li>时间：周一至周五 8:00 - 12:00，13:30 - 17:00(节假日除外)</li><li>地点：门诊四楼下行扶梯处</li></ul><h3 id="办理《出生医学证明》的方式" tabindex="-1">办理《出生医学证明》的方式？ <a class="header-anchor" href="#办理《出生医学证明》的方式" aria-label="Permalink to &quot;办理《出生医学证明》的方式？&quot;">​</a></h3><ul><li>网上预约挂号： <ul><li>重庆市妇幼保健院公众号</li><li>→就诊服务</li><li>→预约挂号</li><li>→冉家坝院区</li><li>→专科预约</li><li>→出生医学证明签发 (列表最末尾)</li><li>→出生医学证明签发处</li><li>→普通号</li><li>→选择时段</li><li>→挂号成功</li><li>→办理当天扫码签到</li></ul></li></ul><hr><h3 id="新生儿医保" tabindex="-1">新生儿医保 <a class="header-anchor" href="#新生儿医保" aria-label="Permalink to &quot;新生儿医保&quot;">​</a></h3><ul><li>需要户口本（当地派出所办理户口本）和出生证明</li><li>渝快办申请</li><li>医保办理成功后，5-7 个工作日才会生效</li></ul><h2 id="相关链接" tabindex="-1">相关链接 <a class="header-anchor" href="#相关链接" aria-label="Permalink to &quot;相关链接&quot;">​</a></h2><ul><li><a href="https://mp.weixin.qq.com/s/mPe-2Yt3L7-4DCxcQXtPwg" target="_blank" rel="nofollow noopener noreferrer">【孕妈妈知多少】宝宝的人生第一证（出生医学证明），速速来领取吧！</a></li></ul>',15)]))}const p=a(r,[["render",o]]);export{m as __pageData,p as default};
