import{_ as e,c as l,a3 as a,o as i}from"./chunks/framework.BOyF8YV7.js";const r="/assets/diapers-usage.ijDEkGGT.jpg",n="/assets/diapers-size0.LNJK4inK.jpg",s="/assets/diapers-size.CJQ8k3-m.jpg",d="/assets/diapers-size2.Bo6da2gG.jpg",c="/assets/diapers-usage-2.C22MR5-h.jpg",o="/assets/diapers-functional.CfwcjOJx.jpg",_=JSON.parse('{"title":"宝宝纸尿裤囤货攻略：尺码、用量、囤货量全搞定！","description":"这篇攻略教你如何根据宝宝月龄和体重选择合适的纸尿裤尺码，并提供不同阶段的纸尿裤用量及囤货建议，帮你轻松搞定宝宝纸尿裤囤货问题，避免浪费，同时包含换纸尿裤技巧及注意事项。","frontmatter":{"head":[["link",{"rel":"canonical","href":"https://blog.ranchulin.com/生活/育儿/纸尿裤用量.html"}],["meta",{"property":"og:title","title":"宝宝纸尿裤囤货攻略：尺码、用量、囤货量全搞定！"}],["meta",{"property":"og:url","content":"https://blog.ranchulin.com/生活/育儿/纸尿裤用量.html"}],["meta",{"name":"keywords","content":"宝宝纸尿裤,纸尿裤囤货,纸尿裤用量,纸尿裤尺码,新生儿纸尿裤,NB码,S码,M码,L码,XL码,XXL码,XXXL码,拉拉裤,换纸尿裤,红屁屁,纸尿裤挑选"}]]},"headers":[],"relativePath":"生活/育儿/纸尿裤用量.md","filePath":"生活/育儿/纸尿裤用量.md","lastUpdated":1736299776000}'),h={name:"生活/育儿/纸尿裤用量.md"};function g(u,t,x,p,y,m){return i(),l("div",null,t[0]||(t[0]=[a('<h3 id="必看-一篇搞定宝宝纸尿裤囤货攻略。" tabindex="-1">必看！一篇搞定宝宝纸尿裤囤货攻略。 <a class="header-anchor" href="#必看-一篇搞定宝宝纸尿裤囤货攻略。" aria-label="Permalink to &quot;必看！一篇搞定宝宝纸尿裤囤货攻略。&quot;">​</a></h3><ul><li>NB 码：0-1 个月，适合 5kg 以下体重的宝宝 <ul><li>新生儿纸尿裤用量大，每天大概 10-12 片，先囤 2 大包，150-200 片左右。</li></ul></li><li>S 码：2-3 个月，适合 4-8kg 的宝宝 <ul><li>日用量 8-10 片，这个时期宝宝长得快，很快就能换 M 码了，4 包就够啦。</li></ul></li><li>M 码：4-6 个月，适合 6-11kg 的宝宝 <ul><li>日用量 6-8 片，囤 4-5 包就够用啦。</li></ul></li><li>L 码：7-12 个月，适合 9-14kg 的宝宝 <ul><li>日用量 6-7 片，宝宝到了爬行期，可以换成拉拉裤啦，纸尿裤囤 6 包，拉拉裤 4 包。</li></ul></li><li>XL 码：12-24 个月，适合 12-17kg 的宝宝 <ul><li>日用量 5-6 片，宝宝站立期尺码稳定，按箱囤！囤个 300 片。</li></ul></li><li>XXL、XXXL 码：18 个月以上，适合 15kg 以上的宝宝 <ul><li>日用量 3-5 片，这个阶段尺码稳定，没有自主如厕打算的话囤 4 包，用完再买。</li></ul></li></ul><h3 id="绘制成表格" tabindex="-1">绘制成表格 <a class="header-anchor" href="#绘制成表格" aria-label="Permalink to &quot;绘制成表格&quot;">​</a></h3><table tabindex="0"><thead><tr><th style="text-align:center;">尺码</th><th style="text-align:center;">月份</th><th style="text-align:center;">适合体重</th><th style="text-align:center;">数量（日用量）</th><th style="text-align:center;">囤积</th></tr></thead><tbody><tr><td style="text-align:center;">NB 码</td><td style="text-align:center;">0-1 个月</td><td style="text-align:center;">5kg 以下体重的宝宝</td><td style="text-align:center;">每天大概 10-12 片</td><td style="text-align:center;">先囤 2 大包，150-200 片左右</td></tr><tr><td style="text-align:center;">S 码</td><td style="text-align:center;">2-3 个月</td><td style="text-align:center;">4-8kg</td><td style="text-align:center;">8-10 片</td><td style="text-align:center;">4 包</td></tr><tr><td style="text-align:center;">M 码</td><td style="text-align:center;">4-6 个月</td><td style="text-align:center;">6-11kg</td><td style="text-align:center;">6-8 片</td><td style="text-align:center;">4-5 包</td></tr><tr><td style="text-align:center;">L 码</td><td style="text-align:center;">7-12 个月</td><td style="text-align:center;">9-14kg</td><td style="text-align:center;">6-7 片</td><td style="text-align:center;">纸尿裤囤 6 包，拉拉裤 4 包</td></tr><tr><td style="text-align:center;">XL 码</td><td style="text-align:center;">12-24 个月</td><td style="text-align:center;">12-17kg</td><td style="text-align:center;">5-6 片</td><td style="text-align:center;">囤个 300 片</td></tr><tr><td style="text-align:center;">XXL、XXXL 码</td><td style="text-align:center;">18 个月以上</td><td style="text-align:center;">15kg</td><td style="text-align:center;">3-5 片</td><td style="text-align:center;">4 包</td></tr></tbody></table><hr><h3 id="什么时候需要检查换纸尿裤" tabindex="-1">什么时候需要检查换纸尿裤 <a class="header-anchor" href="#什么时候需要检查换纸尿裤" aria-label="Permalink to &quot;什么时候需要检查换纸尿裤&quot;">​</a></h3><ul><li>吃奶前和吃奶后 10 分钟、入睡前和睡醒后、外出前、小便 2-3h 一次、拉臭臭后。</li></ul><h3 id="更换纸尿裤要注意什么" tabindex="-1">更换纸尿裤要注意什么 <a class="header-anchor" href="#更换纸尿裤要注意什么" aria-label="Permalink to &quot;更换纸尿裤要注意什么&quot;">​</a></h3><ul><li>男宝女宝擦拭有区别，洗好屁屁要晾干，发现红屁屁不要用湿巾擦。</li></ul><h3 id="什么时候需要换纸尿裤尺码" tabindex="-1">什么时候需要换纸尿裤尺码 <a class="header-anchor" href="#什么时候需要换纸尿裤尺码" aria-label="Permalink to &quot;什么时候需要换纸尿裤尺码&quot;">​</a></h3><ul><li>总是侧漏、大腿根有勒痕、魔术贴贴到最外面、腰围在肚脐以下</li></ul><h3 id="各个阶段纸尿裤的用量及囤货" tabindex="-1">各个阶段纸尿裤的用量及囤货 <a class="header-anchor" href="#各个阶段纸尿裤的用量及囤货" aria-label="Permalink to &quot;各个阶段纸尿裤的用量及囤货&quot;">​</a></h3><p><img src="'+r+'" alt=""></p><h3 id="纸尿裤的大小" tabindex="-1">纸尿裤的大小 <a class="header-anchor" href="#纸尿裤的大小" aria-label="Permalink to &quot;纸尿裤的大小&quot;">​</a></h3><p><img src="'+n+'" alt=""></p><p><img src="'+s+'" alt=""></p><p><img src="'+d+'" alt=""></p><h3 id="纸尿裤常见问题" tabindex="-1">纸尿裤常见问题 <a class="header-anchor" href="#纸尿裤常见问题" aria-label="Permalink to &quot;纸尿裤常见问题&quot;">​</a></h3><p><img src="'+c+'" alt=""></p><h3 id="纸尿裤如何挑选" tabindex="-1">纸尿裤如何挑选 <a class="header-anchor" href="#纸尿裤如何挑选" aria-label="Permalink to &quot;纸尿裤如何挑选&quot;">​</a></h3><p><img src="'+o+'" alt=""></p>',21)]))}const b=e(h,[["render",g]]);export{_ as __pageData,b as default};
