import{_ as a,c as i,a3 as e,o as t}from"./chunks/framework.BOyF8YV7.js";const m=JSON.parse('{"title":"海绵阅读法：七大能力助你高效阅读，建立知识体系并实现知识变现","description":"掌握海绵阅读法的七大能力，提升阅读效率和深度，建立个人知识体系，并学会将阅读知识输出，最终实现知识变现。文章详细介绍了高效做读书笔记的方法，包括万能读书笔记模板九要素，以及如何进行独立思考，提升阅读速度，并持续稳定地进行阅读。","frontmatter":{"head":[["link",{"rel":"canonical","href":"https://blog.ranchulin.com/读后感/2024/海绵阅读法.html"}],["meta",{"property":"og:title","title":"海绵阅读法：七大能力助你高效阅读，建立知识体系并实现知识变现"}],["meta",{"property":"og:url","content":"https://blog.ranchulin.com/读后感/2024/海绵阅读法.html"}],["meta",{"name":"keywords","content":"读书笔记,高效阅读,阅读方法,知识体系,海绵阅读法,深度阅读,阅读输出,独立思考,阅读速度,知识消化,读书技巧,高效学习,阅读能力提升,公众号写作,知识变现"}]]},"headers":[],"relativePath":"读后感/2024/海绵阅读法.md","filePath":"读后感/2024/海绵阅读法.md","lastUpdated":1736299776000}'),r={name:"读后感/2024/海绵阅读法.md"};function o(h,l,u,n,d,c){return t(),i("div",null,l[0]||(l[0]=[e('<h3 id="读书要输出" tabindex="-1">读书要输出 <a class="header-anchor" href="#读书要输出" aria-label="Permalink to &quot;读书要输出&quot;">​</a></h3><ul><li>读这本书，和没读这本书，对我来说有什么区别？</li></ul><h3 id="建立知识体系" tabindex="-1">建立知识体系 <a class="header-anchor" href="#建立知识体系" aria-label="Permalink to &quot;建立知识体系&quot;">​</a></h3><ul><li>《如何阅读一本书》方法主题阅读 <ul><li>把多本书作为一个整体来读，局部阅读。跳出单本书的局限，站在更高的维度做整体的阅读规划，开始慢慢学会如果构建最大程度服务于人生的知识体系。</li></ul></li></ul><h3 id="公众号写作和变现阶段" tabindex="-1">公众号写作和变现阶段 <a class="header-anchor" href="#公众号写作和变现阶段" aria-label="Permalink to &quot;公众号写作和变现阶段&quot;">​</a></h3><ul><li>持续、高质量的阅读输出，对书籍的理解要更深入，思考要有更高的质量，表达要更完整和准确，在这个过程中阅读能力也能得到进一步的锤炼。</li></ul><h3 id="形成系统方法论阶段" tabindex="-1">形成系统方法论阶段 <a class="header-anchor" href="#形成系统方法论阶段" aria-label="Permalink to &quot;形成系统方法论阶段&quot;">​</a></h3><ul><li>思考是需要养分的 <ul><li>多元信息</li><li>高质量信息</li></ul></li></ul><h2 id="海绵阅读法的七大能力" tabindex="-1">海绵阅读法的七大能力 <a class="header-anchor" href="#海绵阅读法的七大能力" aria-label="Permalink to &quot;海绵阅读法的七大能力&quot;">​</a></h2><h3 id="高效做读书笔记的能力" tabindex="-1">高效做读书笔记的能力 <a class="header-anchor" href="#高效做读书笔记的能力" aria-label="Permalink to &quot;高效做读书笔记的能力&quot;">​</a></h3><ul><li><p>“对抗阅读”，“一步三回头”，大大拖慢了阅读速度，而且大部分的内容还是会悄无声息地淡出记忆。</p></li><li><p>做笔记应该起到两个作用</p><ul><li>帮助当下的理解</li><li>帮助以后的记忆</li></ul></li><li><p>作者告诉了我们？</p><ul><li>怎样做笔记</li><li>知识的消化经历了多个消化过程，最终被吸收，如果人类消化食物一样 <ul><li>咀嚼 - 初步消化</li><li>胃液溶解 - 再次消化</li><li>小肠蠕动 - 深度消化吸收</li></ul></li><li>瞬时记忆变成短时记忆 <ul><li>主动学习知识，对知识充分且深度的加工 <ul><li>筛选，筛选有用的信息，重要的信息</li><li>分类，组块化</li><li>联结及复述，通过联想，将新的知识和已有的知识网络，生活经验结合起来，并用自己的话复述出来。</li></ul></li></ul></li><li>短时记忆变成长时记忆 <ul><li>重复</li></ul></li></ul></li><li><p>万能读书笔记模板九要素</p><ul><li>阅读动机 <ul><li>我为什么要读这本书</li><li>是什么让我对这本书感兴趣</li><li>我对这本书有什么期待</li><li>我想从这本书中得到什么</li><li>这本书能给我什么</li></ul></li><li>核心概念（核心人物和核心情节）</li><li>金句收集</li><li>故事案例</li><li>联想发散 <ul><li>其他地方看到的一句话，一个新闻，一个比喻，或者自己的亲身经历</li></ul></li><li>任何疑问</li><li>洞见时刻</li><li>情绪感受</li><li>行动灵感</li></ul></li></ul><h3 id="独立思考能力" tabindex="-1">独立思考能力 <a class="header-anchor" href="#独立思考能力" aria-label="Permalink to &quot;独立思考能力&quot;">​</a></h3><ul><li>精读离不开思考</li></ul><h3 id="掌握阅读速度的能力" tabindex="-1">掌握阅读速度的能力 <a class="header-anchor" href="#掌握阅读速度的能力" aria-label="Permalink to &quot;掌握阅读速度的能力&quot;">​</a></h3><h3 id="阅读不同书籍的能力" tabindex="-1">阅读不同书籍的能力 <a class="header-anchor" href="#阅读不同书籍的能力" aria-label="Permalink to &quot;阅读不同书籍的能力&quot;">​</a></h3><h3 id="建立知识体系的能力" tabindex="-1">建立知识体系的能力 <a class="header-anchor" href="#建立知识体系的能力" aria-label="Permalink to &quot;建立知识体系的能力&quot;">​</a></h3><h3 id="让读书有用的能力" tabindex="-1">让读书有用的能力 <a class="header-anchor" href="#让读书有用的能力" aria-label="Permalink to &quot;让读书有用的能力&quot;">​</a></h3><h3 id="长期持续稳定阅读的能力" tabindex="-1">长期持续稳定阅读的能力 <a class="header-anchor" href="#长期持续稳定阅读的能力" aria-label="Permalink to &quot;长期持续稳定阅读的能力&quot;">​</a></h3>',18)]))}const b=a(r,[["render",o]]);export{m as __pageData,b as default};
